use std::collections::HashSet;
const INPUT: &str = r#""#;

// replace 14 by 4 for part 1
const SEQ_LEN: usize = 14;

fn main() {
    let char_iter: Vec<_> = INPUT.chars().collect();
    for (idx, w) in (&char_iter).windows(SEQ_LEN).enumerate() {
        let hs: HashSet<_> = w.iter().collect();
        if hs.len() == SEQ_LEN {
            println!("{}", idx + SEQ_LEN);
            return;
        }
    }
}
